// Allow importing CSS modules without TypeScript errors
declare module '*.module.css';
declare module '*.module.scss';
declare module '*.module.sass';

// Allow importing plain CSS (if used)
declare module '*.css';

// Allow image/svg imports commonly used in Next projects
declare module '*.svg';
declare module '*.png';
declare module '*.jpg';
declare module '*.jpeg';
declare module '*.gif';
